mapper.select:
select id, max_amount as maxAmount, min_amount as minAmount, amount_limit.limit_college as college, project_type as projectType, time_limit.start_time as startTime, time_limit.end_time as endTime from openexperiment.amount_limit left join openexperiment.time_limit on amount_limit.limit_college = time_limit.limit_college and time_limit.limit_type = 8
limit_college = #{college}
and project_type = #{type]
