mapper.select:
select id as v_id, max_amount as v_max_amount, min_amount as v_min_amount, project_type as v_project_type, amount_limit.limit_college, time_limit.start_time, time_limit.end_time from openexperiment.amount_limit left join openexperiment.time_limit on amount_limit.limit_college = time_limit.limit_college and time_limit.limit_type = 8
limit_college = #{college}
and project_type = #{type]
