mapper.update:
update operation_record set status = 'N'
where related_id = #{projectId}

   and operation_type = #{type}

mapper.select:
select id as id, read_status as readStatus, operation_type as operationType, operation_unit as operationUnit, operation_reason as reason, operation_time as operationTime, operation_executor_id as operationExecutorId, related_id as projectId from operation_record where related_id in
#{id}
and `status` = 'Y'
